        <div class="dashvio-scroll-fade" style="background: white; padding: 3rem; border-radius: 16px; box-shadow: 0 4px 20px rgba(0,0,0,0.08);">
            <h3 style="font-size: 1.75rem; font-weight: 600; margin-bottom: 2rem; text-align: center; color: var(--cosmetics-dark);">Send Us a Message</h3>
            <form style="display: flex; flex-direction: column; gap: 1.5rem;">
                <div style="display: grid; grid-template-columns: 1fr 1fr; gap: 1.5rem;">
                    <input type="text" placeholder="Your Name" style="padding: 1rem; border: 2px solid rgba(255, 182, 193, 0.3); border-radius: 8px; font-size: 1rem; outline: none; transition: border-color 0.3s;" onfocus="this.style.borderColor='var(--cosmetics-primary)'" onblur="this.style.borderColor='rgba(255, 182, 193, 0.3)'" />
                    <input type="email" placeholder="Your Email" style="padding: 1rem; border: 2px solid rgba(255, 182, 193, 0.3); border-radius: 8px; font-size: 1rem; outline: none; transition: border-color 0.3s;" onfocus="this.style.borderColor='var(--cosmetics-primary)'" onblur="this.style.borderColor='rgba(255, 182, 193, 0.3)'" />
                </div>
                <input type="text" placeholder="Subject" style="padding: 1rem; border: 2px solid rgba(255, 182, 193, 0.3); border-radius: 8px; font-size: 1rem; outline: none; transition: border-color 0.3s;" onfocus="this.style.borderColor='var(--cosmetics-primary)'" onblur="this.style.borderColor='rgba(255, 182, 193, 0.3)'" />
                <textarea placeholder="Your Message" rows="5" style="padding: 1rem; border: 2px solid rgba(255, 182, 193, 0.3); border-radius: 8px; font-size: 1rem; outline: none; resize: vertical; transition: border-color 0.3s;" onfocus="this.style.borderColor='var(--cosmetics-primary)'" onblur="this.style.borderColor='rgba(255, 182, 193, 0.3)'"></textarea>
                <button type="submit" class="demo-btn demo-btn--cosmetics" style="padding: 1rem 2rem; font-size: 1rem; align-self: center; min-width: 200px;">Send Message</button>
            </form>
